commits.content:
.about-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1.25rem; // 20px

  @media (min-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (min-width: 1280px) {
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(6, auto);
  }
}


.grid-container {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.img-full {
  width: 100%;
  height: auto;
}

.sm-img-tall {
  @media (min-width: 640px) {
    height: 276px;
  }
}

.sm-img-mid {
  @media (min-width: 640px) {
    height: 266px;
  }
}

.sm-img-fixed {
  @media (min-width: 640px) {
    width: 276px;
  }
}


.about-headtext {
  font-size: 1.5rem;
  font-weight: bold;

}

.about-subtext {
  font-size: 1rem;

}
