commits.content:
.hero-section {
  min-height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  position: relative;
}

.hero-content {
  width: 100%;
  margin: 5rem auto 0; // equivalent to mt-20
  padding-left: 1.5rem;
  padding-right: 1.5rem;
  display: flex;
  flex-direction: column;
  gap: 0.75rem;

  @media (min-width: 640px) {
    margin-top: 9rem; // sm:mt-36
  }
}

.hero-greeting {
  font-size: 1.5rem;
  font-weight: 500;
  color: white;
  text-align: center;
  font-family: 'GeneralSans', sans-serif;

  @media (min-width: 640px) {
    font-size: 1.875rem;
  }
}

.hero-tag {
  text-align: center;
  background: linear-gradient(to right, #ccc, #fff); // fallback
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  font-size: 1rem;
  margin-top: 0.5rem;
}

.canvas-container {
  width: 100%;
  height: 100%;
  position: absolute;
  inset: 0;
  z-index: 1;
}
