@import 'variables';

*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    background-image: linear-gradient(to bottom, #1A1B2E, #202334 75%, #2F3244 100%);
}

body{
    font-family: "Forum", serif;
    color: variables.$colorIvory;
}
