+@use 'variables' as *;
+
+.skill-container{
+
+    .potion-container{
+        text-align: center;
+        margin: 2rem;
+
+    .potion{
+        position: relative;
+        width: 60px;
+        height: 100px;
+        background-color: rgba(255, 255, 255, 0.05);
+        border: 2px solid #f6e6b4;
+        border-radius: 20px 20px 10px 10px;
+        overflow: hidden;
+        box-shadow: 0 0 8px #89A9D0;
+
+        .liquid{
+            position: absolute;
+            bottom: 0;
+            width: 100%;
+            height: 60%;
+            background: linear-gradient(to top, #b9a9d0, #f6e6b4);
+            animation: wave 4s ease-in-out infinite;
+        }
+
+        .bubbles::before,
+        .bubbles::after{
+            content: "";
+            position: absolute;
+            width: 6px;
+            height: 6px;
+            border-radius: 50%;
+            background: #f6e6b4;
+            animation: bubble 2s infinite ease-in-out;
+        }
+
+        .bubbles::after{
+            left: 30px;
+            animation-delay: 1s;
+        }
+
+        .label{
+            margin-top: 0.5rem;
+            color: #f6e6b4;
+        }
+
+    }
+
+    }
+}
+
+@keyframes wave{
+    0%, 100%{transform: translateY(0);}
+    50%{transform: translateY(-5px);}
+}
+
+@keyframes bubble{
+  0% { bottom: 0; opacity: 0.8; transform: scale(1); }
+  50% { bottom: 40px; opacity: 0.4; transform: scale(1.2); }
+  100% { bottom: 60px; opacity: 0; transform: scale(0.8); }
+}
